import { useState } from 'react';
import styles from './SteamerForm.module.scss';

const StreamerForm = () => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [platform, setPlatform] = useState('Twitch');

  return (
    <form>
      <input className={styles.name} type='text' name="name" value={name} onChange={(e) => setName(e.target.value)}/>
      <select value={platform} className={styles.platform} onChange={(e) => setPlatform(e.target.value)}>
        <option selected value="Twitch">Twitch</option>
        <option value="YouTube">YouTube</option>
        <option value="TikTok">TikTok</option>
        <option value="Kick">Kick</option>
        <option value="Rumble">Rumble</option>
      </select>
      <textarea className={styles.description} name="description" value={description} onChange={(e) => setDescription(e.target.value)}/>
    </form>
  )
}

export default StreamerForm
