import { useDispatch, useSelector } from "react-redux"
import { useEffect } from "react"
import { fetchSteamers } from "../../redux/slice/streamerSlice"
const StreamerList = ({children}) => {
  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(fetchSteamers())
  }, [dispatch])

  const streamers = useSelector((state) => {return state.streamer.streamers})
  const isLoading = useSelector((state) => state.streamer.isLoading)
  const error = useSelector((state) => state.streamer.error)

  if (isLoading) {
    return 'loading...'
  }

  if (error) {
    return error
  }

  return (
    <>
      {streamers.map((streamer, key) => (
        <div key={key}>
            {streamer.name}
        </div>
      ))}
    </>
  )
}

export default StreamerList;
