import styles from './Vote.module.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowDown, faArrowUp } from '@fortawesome/free-solid-svg-icons'
import { useDispatch, useSelector } from 'react-redux';
import { voteSteamer } from '../../redux/slice/streamerSlice';

const Vote = ({ streamer }) => {
  const dispatch = useDispatch();

  const isLoading = useSelector((state) => state.streamers.voteLoading);
  const error = useSelector((state) => state.streamers.voteError);
  const userId = useSelector((state) => state.auth.userId);

  const handleUpvote = (e) => {
    dispatch(voteSteamer({id: streamer._id, vote: 1}))
  }

  const handleDownvote = (e) => {
    dispatch(voteSteamer({id: streamer._id, vote: 0}))
  }

  if (isLoading) {
    return 'loading...'
  }

  if (error) {
    return error
  }

  return (
    <div className={styles.vote}>
    { streamer.hasOwnProperty('vote') &&
      <>
        <button onClick={handleUpvote} style={{color: `${streamer.vote.positive.includes(userId) && 'green'}`}} className={styles.vote__button}>
          <FontAwesomeIcon icon={faArrowUp} />
        </button>
        <div className={styles.vote__amount}>{streamer.vote.positive.length - streamer.vote.negative.length}</div>
        <button onClick={handleDownvote}  style={{color: `${streamer.vote.negative.includes(userId) && 'red'}`}} className={styles.vote__button}>
          <FontAwesomeIcon icon={faArrowDown} />
        </button>
      </>
    }
    </div>
  )
};

export default Vote;
