import { useState } from 'react';
import styles from './SteamerForm.module.scss';
import { addStreamer } from '../../redux/slice/streamerSlice';
import { useDispatch } from 'react-redux';

const StreamerForm = () => {
  const dispatch = useDispatch();
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [platform, setPlatform] = useState('Twitch');

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(addStreamer({name, description, platform}))
  }

  return (
    <form>
      <input className={styles.name} type='text' name="name" value={name} onChange={(e) => setName(e.target.value)}/>
      <select name="platform" value={platform} className={styles.platform} onChange={(e) => setPlatform(e.target.value)}>
        <option defaultValue value="Twitch">Twitch</option>
        <option value="YouTube">YouTube</option>
        <option value="TikTok">TikTok</option>
        <option value="Kick">Kick</option>
        <option value="Rumble">Rumble</option>
      </select>
      <textarea className={styles.description} name="description" value={description} onChange={(e) => setDescription(e.target.value)}/>
      <input type="submit" value="Submit"/>
    </form>
  )
}

export default StreamerForm
